package meercode;
import java.util.*;
import java.io.*;
import meercode.ast.*;
public class App {
    public String getGreeting() {
        return "Hello world.";
    }

    public static void main(String[] args) {
        AnnotatedSyntaxTree tree = new AnnotatedSyntaxTree(new Node("+"));
        tree.getHead().left = new Node("5");
        tree.getHead().right = new Node("*");
        tree.getHead().right.left = new Node("3");
        tree.getHead().right.right = new Node("7");
        String output = "./TestOutput.txt";
        try
        {
            System.out.println("Making reader");
            ASTReader reader = new ASTReader(tree, output);
            reader.readTree();
            reader.testWrite();
        }
        catch (Exception E)
        {
            System.out.println("IO Exception occured");
        }
    }
}
